clc;clear all;close all;m = 1920;n = 1080;ib = imread('RGB_abstruct.jpg');figure(1);imshow(ib);double tmp;i1 = zeros(m, n);fp = fopen('testImage_Results_RGB.txt', 'r');tmp = fscan(fp, '%d');tmp = tmp';for i = 1 : mfor j = 1 : ni1(i, j) = tmp((i - 1) * n + j);endenddouble tmpB;iB = zeros(m, n);fpB = fopen('testImageResults_BLUR_RGB.txt', 'r');tmpB = fscan(fpB, '%d');tmpB = tmpB';for i = 1 : mfor j = 1 : niB(i, j) = tmpB((i - 1) * n + j);endendfclose(fpB);figure(3);imshow(iB, [])